(95, 'HEO ĐẤT TIẾT KIỆM', '<h3><span style="font-size:16px"><strong><a href="http://bibomart.com.vn/heo-dat-tiet-kiem-be-p40958.html">Heo đất tiết kiệm</a>&nbsp;được l&agrave;m từ chất liệu sứ an to&agrave;n, l&agrave; m&oacute;n qu&agrave; đầy &yacute; nghĩa với tuổi thơ của c&aacute;c b&eacute;. Sở hữu 1 ch&uacute; heo đất xinh xắn gi&uacute;p b&eacute; học c&aacute;ch tiết kiệm tiền ngay từ khi c&ograve;n nhỏ, đ&acirc;y cũng l&agrave; m&oacute;n đồ trang tr&iacute; cho căn ph&ograve;ng của b&eacute; th&ecirc;m nhiều m&agrave;u sắc.</strong></span></h3>\r\n\r\n<p style="text-align: center;">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><span style="font-size:16px"><img alt="" src="http://media.bibomart.com.vn/u/bbm/product/2015/12/23/22/33/410_410/heo-dat-tiet-kiem-be-112273_1.jpg" /></span></p>\r\n\r\n<p style="text-align: center;">&nbsp;</p>\r\n\r\n<p><span style="font-size:16px"><strong>Chất liệu an to&agrave;n</strong></span></p>\r\n\r\n<p><span style="font-size:16px">Heo đất nằm to được l&agrave;m từ chất liệu s&agrave;nh sứ c&ugrave;ng nước sơn an to&agrave;n, ba mẹ ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m về sức khỏe của b&eacute; khi cho b&eacute; chơi đ&ugrave;a cũng heo đất.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:16px"><strong>L&agrave; m&oacute;n&nbsp;<a href="http://bibomart.com.vn/do-choi-tre-em-t513.html" title="đồ chơi">đồ chơi</a>&nbsp;y&ecirc;u th&iacute;ch của tuổi thơ</strong></span></p>\r\n\r\n<p><span style="font-size:16px">Heo đất lu&ocirc;n l&agrave; m&oacute;n&nbsp;<a href="http://bibomart.com.vn/be-choi-ma-hoc-c131.html" title="đồ chơi">đồ chơi</a>&nbsp;y&ecirc;u th&iacute;ch của c&aacute;c b&eacute; thơ, gi&uacute;p c&aacute;c b&eacute; học c&aacute;ch tiết kiệm tiền ngay từ khi c&ograve;n nhỏ</span></p>\r\n\r\n<p><span style="font-size:16px">Ngo&agrave;i l&agrave; m&oacute;n đồ chơi hữu &iacute;ch, heo đất c&ograve;n l&agrave; vật trang tr&iacute; cho căn ph&ograve;ng của b&eacute; th&ecirc;m ngộ nghĩnh, nhiều m&agrave;u sắc.</span></p>\r\n\r\n<p style="text-align: center;">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><span style="font-size:16px"><img alt="" src="http://media.bibomart.com.vn/u/bbm/product/2015/12/23/22/33/410_410/heo-dat-tiet-kiem-be-112273-1_1.jpg" /></span></p>\r\n', '30', '1452887008heo-dat-tiet-kiem-be-112273_1.jpg', '-1'),
(96, 'BÚP BÊ CHÂN NGẮN', '<h3><span style="font-size:16px"><strong><a href="http://bibomart.com.vn/bup-be-chan-ngan-p40963.html">B&uacute;p b&ecirc; ch&acirc;n ngắn</a>&nbsp;với thiết kế nhỏ gọn, b&eacute; c&oacute; thể m&oacute;c v&agrave;o cặp s&aacute;ch, balo hay những m&oacute;n đồ dễ thương của b&eacute;, b&uacute;p b&ecirc; sẽ b&ecirc;n cạnh b&eacute; mọi l&uacute;c, mọi nơi... Với thiết kế đ&aacute;ng y&ecirc;u c&ugrave;ng những bộ trang phục nổi bật, b&uacute;p b&ecirc; sẽ l&agrave; người bạn th&acirc;n thiết với tuổi thơ của b&eacute;.</strong></span></h3>\r\n\r\n<p style="text-align:center"><span style="font-size:16px"><img alt="" src="http://media.bibomart.com.vn/u/bbm/product/2015/12/24/16/24/410_410/bup-be-chan-ngan-112325-3_1.jpg" /></span></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p><span style="font-size:16px"><strong>Thiết kế đẹp mắt</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:16px"><strong>B&uacute;p b&ecirc; ch&acirc;n ngắn</strong>&nbsp;với bộ trang phục xinh xắn, mang đến sự th&iacute;ch th&uacute; cho b&eacute; khi được tự do thay những bộ trang phục thật phong c&aacute;ch cho b&uacute;p b&ecirc;.</span></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p><span style="font-size:16px"><strong>Chất liệu cao cấp</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:16px">Được l&agrave;m từ vải v&agrave; nhựa cao cấp,&nbsp;<strong>B&uacute;p b&ecirc; ch&acirc;n ngắn</strong>&nbsp;bảo đảm an to&agrave;n cho b&eacute;, kh&ocirc;ng g&acirc;y độc hại, ảnh hưởng đến sức khỏe của b&eacute;.</span></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><span style="font-size:16px"><img alt="" src="http://media.bibomart.com.vn/u/bbm/product/2015/12/24/16/24/410_410/bup-be-chan-ngan-112325_1.jpg" /></span></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p><span style="font-size:16px"><strong>Gi&uacute;p ph&aacute;t triển tư duy v&agrave; khả năng s&aacute;ng tạo của b&eacute;</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:16px">Với tr&ograve; chơi n&agrave;y, b&eacute; c&oacute; thể thỏa sức tưởng tượng c&aacute;c kiểu d&aacute;ng th&uacute; vị cho n&agrave;ng b&uacute;p b&ecirc;, qua đ&oacute; khả năng tư duy v&agrave; &oacute;c s&aacute;ng tạo của b&eacute; sẽ được ph&aacute;t huy tối đa, đồng thời gi&uacute;p b&eacute; thật kh&eacute;o tay khi l&agrave;m những việc đ&ograve;i hỏi sự kh&eacute;o l&eacute;o.</span></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p><span style="font-size:16px">B&uacute;p b&ecirc; với m&oacute;c kh&oacute;a đi k&egrave;m gi&uacute;p b&eacute; dễ d&agrave;ng mang b&uacute;p b&ecirc; đi c&ugrave;ng mọi nơi</span></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><span style="font-size:16px"><img alt="" src="http://media.bibomart.com.vn/u/bbm/product/2015/12/24/16/24/410_410/bup-be-chan-ngan-112325-2_1.jpg" /></span></p>\r\n', '40', '1452887073bup-be-chan-ngan-112325-3_1.jpg', '-1'),
(97, 'THÚ BÔNG MOMCARE', '<p><strong>Đồ chơi&nbsp;kh&ocirc;ng chỉ l&agrave; một người bạn c&ugrave;ng b&eacute; lớn l&ecirc;n m&agrave; c&ograve;n l&agrave; &quot;người thầy&quot; gi&uacute;p b&eacute; t&igrave;m hiểu v&agrave; kh&aacute;m ph&aacute; thế giới xung quanh.Th&uacute; b&ocirc;ng Momcare&nbsp;l&agrave; m&oacute;n đồ chơi th&iacute;ch hợp với c&aacute;c b&eacute; dưới 1 tuổi. Th&uacute; b&ocirc;ng được l&agrave;m từ chất liệu cao cấp, an to&agrave;n cho b&eacute; khi chơi với thiết kế cực k&igrave; xinh xắn, đ&aacute;ng y&ecirc;u.</strong></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://media.bibomart.com.vn/u/bbm/product/2016/01/12/20/54/410_410/thu-bong-momcare-105611-2_1.jpg" /></p>\r\n\r\n<p><strong>Chất liệu an to&agrave;n</strong></p>\r\n\r\n<p>Được nghi&ecirc;n cứu v&agrave; thiết kế bởi đội ngũ chuy&ecirc;n nghiệp trong ng&agrave;nh sản xuất&nbsp;đồ chơi trẻ em, th&uacute; nhồi b&ocirc;ng kh&ocirc;ng những mềm mại m&agrave; c&ograve;n kh&ocirc;ng chứa chất g&acirc;y hại cho sức khỏe của b&eacute;.</p>\r\n\r\n<p><strong>Thiết kế đ&aacute;ng y&ecirc;u, ph&ugrave; hợp với sở th&iacute;ch của b&eacute;</strong></p>\r\n\r\n<p>Được thiết kế thật tinh tế, đường n&eacute;t kh&eacute;o l&eacute;o phối m&agrave;u độc đ&aacute;o, v&agrave; tr&ecirc;n hết dựa v&agrave;o những nh&acirc;n vật trong phim hoạt h&igrave;nh quen thuộc,<strong>Th&uacute; b&ocirc;ng Momcare</strong>&nbsp;vừa l&agrave; m&oacute;n đồ chơi vừa c&oacute; thể trở th&agrave;nh vật trang tr&iacute; cho căn ph&ograve;ng của b&eacute; xinh xắn hơn.</p>\r\n\r\n<p style="text-align:center"><img alt="Thú bông " src="http://media.bibomart.com.vn/u/bbm/product/2016/01/12/20/54/410_410/thu-bong-momcare-105611_1.jpg" /></p>\r\n\r\n<p><strong>Lựa chọn tuyệt vời cho đ&ocirc;i b&agrave;n tay nhỏ của b&eacute;</strong></p>\r\n\r\n<p>K&iacute;ch thước ch&uacute; voi, hổ... vừa cỡ tay b&eacute; để b&eacute; chơi dễ d&agrave;ng, kh&ocirc;ng hại đến cơ tay của b&eacute;&nbsp;sơ sinh.</p>\r\n\r\n<p><strong>Ph&aacute;t triển to&agrave;n diện cho b&eacute;</strong></p>\r\n\r\n<p><strong>Th&uacute; b&ocirc;ng Momcare</strong>&nbsp;ph&aacute;t triển thị gi&aacute;c, th&iacute;nh gi&aacute;c v&agrave; x&uacute;c gi&aacute;c cho b&eacute; y&ecirc;u.</p>\r\n\r\n<p style="text-align:center"><img alt="Thú bông" src="http://media.bibomart.com.vn/u/bbm/product/2016/01/12/20/54/410_410/thu-bong-momcare-105611-1_1.jpg" /></p>\r\n', '145', '1455377050thu-bong-momcare-105611-2_1.jpg', 'Mới');

